private void btnsaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsaveActionPerformed

    /////:::::  COMPROBAR CAMPOS :::::: //////

    if (txtid.getText().equals("")) {
        JOptionPane.showMessageDialog(null, "Por Favor digitar un ID Cliente");
        txtid.requestFocusInWindow();
        return;
    }
    if (!datoNum(txtid.getText())) {
        JOptionPane.showMessageDialog(null, "El ID Cliente no es de tipo numerico");
        txtid.setText("");
        txtid.requestFocusInWindow();
        return;
    }
    if (txtname.getText().equals("")) {
        JOptionPane.showMessageDialog(null, "Por Favor digitar un Nombre(s)");
        txtname.requestFocusInWindow();
        return;
    }
    if (txtlastname.getText().equals("")) {
        JOptionPane.showMessageDialog(null, "Por Favor digitar un Apellido(s)");
        txtlastname.requestFocusInWindow();
        return;
    }
    if (txttel.getText().equals("")) {
        JOptionPane.showMessageDialog(null, "Por Favor digitar un telefóno");
        txttel.requestFocusInWindow();
        return;
    }
    if (!datoNum(txttel.getText())) {
        JOptionPane.showMessageDialog(null, "El Telefono no es de tipo numerico");
        txttel.setText("");
        txttel.requestFocusInWindow();
        return;
    }

    if (txtcel.getText().equals("")) {
        JOptionPane.showMessageDialog(null, "Por Favor digitar un Celular");
        txtcel.requestFocusInWindow();
        return;
    }
    if (!datoNum(txtcel.getText())) {
        JOptionPane.showMessageDialog(null, "El Celular no es de tipo numerico");
        txtcel.setText("");
        txtcel.requestFocusInWindow();
        return;
    }
    if (txtaddres.getText().equals("")) {
        JOptionPane.showMessageDialog(null, "Por Favor digitar un e-mail");
        txtaddres.requestFocusInWindow();
        return;
    }


    if (txtname.getText().equals("NOREGISTRO")) {
        JOptionPane.showMessageDialog(null, "no se registrara el usuario con las siguientes siglas NOREGISTRO ");
        txtname.requestFocusInWindow();
        return;
    }

    ////::: EDITAR REGISTRO Y GENERAR REGISTRO:::://///
    
    if (modificar) {
        if (misDatos.EditarCliente(
                txtid.getText(),
                txtname.getText(),
                txtlastname.getText(),
                txttel.getText(),
                txtcel.getText(),
                txtaddres.getText())) {

            JOptionPane.showMessageDialog(null, "Se ha modificado exitosamente");
        } else {
            JOptionPane.showMessageDialog(null, "No se pudo registrar. Intentelo nuevamente");
        }
    } else {
        if (misDatos.RegistroCliente(
                txtid.getText(),
                txtname.getText(),
                txtlastname.getText(),
                txttel.getText(),
                txtcel.getText(),
                txtaddres.getText())) {

            JOptionPane.showMessageDialog(null, "Registro del cliente correctamenre");
        } else {
            JOptionPane.showMessageDialog(null, "No se pudo registrar el cliente");
            return;
        }

    }


    /////:::::LIMPIAR CAMPOS:::::://////

    txtid.setText("");
    txtname.setText("");
    txtlastname.setText("");
    txttel.setText("");
    txtcel.setText("");
    txtaddres.setText("");

    ///////:::::::::HABILITAR CAMPOS:::::::://///////

    txtid.setEnabled(false);
    txtname.setEnabled(false);
    txtlastname.setEnabled(false);
    txttel.setEnabled(false);
    txtcel.setEnabled(false);
    txtaddres.setEnabled(false);
    ///::::HABILITACION DE BOTONES:::://///
    btnsave.setEnabled(false);
    btncancel.setEnabled(false);

    llenandoTabla();

}//GEN-LAST:event_btnsaveActionPerformed

