private void btndeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndeleteActionPerformed

    String DeleteClient = JOptionPane.showInputDialog("Ingrese ID del Cliente a eliminar.");

    if (DeleteClient.equals("")) {
        return;
    }

    if (misDatos.DeleteClient(DeleteClient)) {
        JOptionPane.showMessageDialog(null, "Cliente Eliminado correctamente");
    } else {
        JOptionPane.showMessageDialog(null, "No se encontro el usuario");
    }

    llenandoTabla();

}//GEN-LAST:event_btndeleteActionPerformed

    private void btnchangeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnchangeActionPerformed

        String change = JOptionPane.showInputDialog(null, "Digitar el ID del Usuario a modificar... =) ");

        if(change.equals("")){
            return;
        }
        
        try {
            ResultSet Datos = misDatos.tblCliente();

            while (Datos.next()) {

                if (Datos.getString("ID_Cliente").equals(change)) {

                    txtid.setText(Datos.getString("ID_Cliente"));
                    txtname.setText(Datos.getString("Nombre"));
                    txtlastname.setText(Datos.getString("Apellido"));
                    txttel.setText(Datos.getString("Telefono"));
                    txtcel.setText(Datos.getString("Celular"));
                    txtaddres.setText(Datos.getString("e_mail"));

                }
            }

        } catch (Exception e) {
            System.out.println("Error de consulta de datos" + e);
        }


        // ::::::::: ACTIVAR CAMPOS  :::::::::: //

        txtid.setEnabled(true);
        txtname.setEnabled(true);
        txtlastname.setEnabled(true);
        txttel.setEnabled(true);
        txtcel.setEnabled(true);
        txtaddres.setEnabled(true);

        ///:::::::::HABILITAR BOTONES::::::////


        btnsave.setEnabled(true);
        btncancel.setEnabled(true);

       modificar=true;





    }//GEN-LAST:event_btnchangeActionPerformed

    private void llenandoTabla() {

        try {

            String titulos[] = {"ID Cliente", "Nombre", "Apellido", "Telefono", "Celular", "Email"}; // crear los titulos
            String Regisgtros[] = new String[6];

            miTabla = new DefaultTableModel(null, titulos); // agrega los titulos a la tabla

            ResultSet rs = misDatos.tblCliente();
            while (rs.next()) {

                Regisgtros[0] = rs.getString("ID_Cliente");
                Regisgtros[1] = rs.getString("Nombre");
                Regisgtros[2] = rs.getString("Apellido");
                Regisgtros[3] = rs.getString("Telefono");
                Regisgtros[4] = rs.getString("Celular");
                Regisgtros[5] = rs.getString("e_mail");
                miTabla.addRow(Regisgtros);

            }
            Tabla.setModel(miTabla);
        } catch (Exception e) {
            System.out.println("Error busqueda de datos tabla de Clientes " + e);
        }

    }

    private boolean datoNum(String num) {

        try {
            Long numero = Long.parseLong(num);
            return true;
        } catch (NumberFormatException e) {
            return false;
        }
    }
